import React, { useEffect, useState, Fragment } from "react";

import axios from "axios";
import { useTranslation } from "react-i18next";
import { Navbar,   Nav } from "react-bootstrap";
import {Link, NavLink} from "react-router-dom"
import PerfectScrollbar from "react-perfect-scrollbar";
import { Scrollbars } from 'react-custom-scrollbars';
import HorizontalScroll from 'react-scroll-horizontal'
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import { Row, Col, Card, Tab } from "react-bootstrap";

import PageTitle from "../../layouts/PageTitle";

// Import css files
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

import Slider from "react-slick";
const ShowBranchDetails = (props) => {
	const { t } = useTranslation();
   const branchId =atob(props.match.params.id);
   // atob(branchNameId)
   const [loading, setLoading]= useState(true); 
   const [data, setData]= useState([])
   const [categories, setCategories]= useState([])
   const [subcategories, setSubCategories]= useState([])
   // const [products, setProducts]= useState([])
   const [variants, setVariants]= useState([])
   
   useEffect(()=>{

      axios.get(`/api/GetBranchForShow/${branchId}`).then(res => {
         if(res.data.status === 200){
            setData(res.data.data);
         }
         setLoading(false);
       });
      axios.get(`/api/GetCategories/${branchId}`).then(res => {
         if(res.data.status === 200){
            setCategories(res.data.fetchData);
         }
       });
       axios.get(`/api/getSubCateBasedOnBranch/${branchId}`).then(res => {
         if(res.data.status === 200){
            // console.log(res.data.fetchData);
            setSubCategories(res.data.fetchData);
            // setProducts([]);
            // setVariants([]);
         }
       });
       axios.get(`/api/GetVariationsBasedOnBranch/${branchId}`).then(res => {
         if(res.data.status === 200){
            setVariants(res.data.fetchData);
            // console.log(res.data.fetchData);
         }
     });
   },[branchId])
   const filterCategory=(cateId)=>{
      axios.get(`/api/GetSubCategories/${cateId}`).then(res => {
         if(res.data.status === 200){
            // console.log(res.data.fetchData);
            setSubCategories(res.data.fetchData);
            // setProducts([]);
            // setVariants([]);
         }
       });
   }
   const filterProducts=(subCateID)=>{
      //  const updateItem=variants.filter((curElem) => {
      //    return curElem.sub_category_id===subCateID;
      // })
      // setVariants(updateItem);

      axios.get(`/api/GetProductsBasedOnSubCategory/${subCateID}`).then(res => {
         if(res.data.status === 200){
            setVariants(res.data.fetchData)
         }
       });
   }
   // const filterVariants = (productID)=>{
   //    axios.get(`/api/Getvariations/${productID}`).then(res => {
   //       if(res.data.status === 200){
   //          setVariants(res.data.fetchData);
   //       }
   //     });

   // }

   var viewShow_HTMLTABLE="";
   if(loading){
      return <div className=""><h4>{t('loading')}</h4></div>
  }else{
      var value;
      viewShow_HTMLTABLE = 
      variants.map((item,i)=>{
         
          return (
            <div className="col-xl-3 col-lg-3 col-md-4 col-sm-4 col-xs-4 col-half-offset"  key={i}>
              {item.ProductName===value ? <h5 className="row mt-2 mx-3 invisible">{ item.ProductName}</h5> : <h5 className="row mt-2 mx-3">{item.ProductName}</h5>}
                  <h6 className="d-none">{value=item.ProductName}</h6>
               <div>
                  
                  <div className="card">
                     <div className="card-body">
                     <div className="new-arrival-product">
                        <div className="new-arrivals-img-contnent">
                              <img className="img-fluid" style={{with:'100px', height:'180px' ,objectFit: 'contain'}} src={`http://192.168.1.103/yesilik1/public/images/variants_pics/${item.PicturesLocation}`} alt="" />
                        </div>
                        <div className="new-arrival-content text-center mt-3">
                              <h4>
                                 <Link to={`/variant-details/${item.variantID}`}  className="text-black "> {item.VariationName}</Link>
                              </h4>
                              <span className="price">{item.CurrentPrice+' '+ item.currency_code}</span>
                              <s className="ms-2">{item.OldPrice +' '+ item.currency_code}</s>
                        </div>
                     </div>
                     </div>
                  </div>
               </div>
            </div>
          )
      })
  }
  
  const settings = {
   dots: false,
   className: "center",
   centerMode: false,
   infinite: true,
   swipeToSlide: true,
   centerPadding: "10px",
   slidesToShow: 6,
   slidesToScroll: 1,
   speed: 1000,
   responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 6,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1
        }
      }
    ]
 }; 
 const tabData = [
   {
      name: "Home",
      icon: "home",
      content:
         "Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts. Separated they live in Bookmarksgrove.",
   },
   {
      name: "Profile",
      icon: "user",
      content:
         "Raw denim you probably haven't heard of them jean shorts Austin. Nesciunt tofu stumptown aliqua, retro synth master cleanse. Mustache cliche tempor.      ",
   },
   {
      name: "Contact",
      icon: "phone",
      content:
         "Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts. Separated they live in Bookmarksgrove.",
   },

   {
      name: "Message",
      icon: "envelope",
      content:
         "Raw denim you probably haven't heard of them jean shorts Austin. Nesciunt tofu stumptown aliqua, retro synth master cleanse. Mustache cliche tempor.      ",
   },
];
   return (
      
      <div >
         <Fragment>
         <PageTitle headingPara="Tab" activeMenu="Tab" motherMenu="Bootstrap" />
         <Row>
            <Col xl={6}>
               <Card>
                  <Card.Header>
                     <Card.Title>Default Tab</Card.Title>
                  </Card.Header>
                  <Card.Body>
                     {/* <!-- Nav tabs --> */}
                     <div className="default-tab">
                        <Tab.Container
                           defaultActiveKey={tabData[0].name.toLowerCase()}
                        >
                           <Nav as="ul" className="nav-tabs">
                              {tabData.map((data, i) => (
                                 <Nav.Item as="li" key={i}>
                                    <Nav.Link
                                       eventKey={data.name.toLowerCase()}
                                    >
                                       <i
                                          className={`la la-${data.icon} mr-2`}
                                       />
                                       {data.name}
                                    </Nav.Link>
                                 </Nav.Item>
                              ))}
                           </Nav>
                           <Tab.Content className="pt-4">
                              {tabData.map((data, i) => (
                                 <Tab.Pane
                                    eventKey={data.name.toLowerCase()}
                                    key={i}
                                 >
                                    <h4>This is {data.name} title</h4>
                                    <p>{data.content}</p>
                                    <p>{data.content}</p>
                                 </Tab.Pane>
                              ))}
                           </Tab.Content>
                        </Tab.Container>
                     </div>
                  </Card.Body>
               </Card>
            </Col>
            <Col xl={6}>
               <Card>
                  <Card.Header>
                     <Card.Title>Custom Tab 1</Card.Title>
                  </Card.Header>
                  <Card.Body>
                     {/* <!-- Nav tabs --> */}
                     <div className="custom-tab-1">
                        <Tab.Container
                           defaultActiveKey={tabData[0].name.toLowerCase()}
                        >
                           <Nav as="ul" className="nav-tabs">
                              {tabData.map((data, i) => (
                                 <Nav.Item as="li" key={i}>
                                    <Nav.Link
                                       eventKey={data.name.toLowerCase()}
                                    >
                                       <i
                                          className={`la la-${data.icon} mr-2`}
                                       />
                                       {data.name}
                                    </Nav.Link>
                                 </Nav.Item>
                              ))}
                           </Nav>
                           <Tab.Content className="pt-4">
                              {tabData.map((data, i) => (
                                 <Tab.Pane
                                    eventKey={data.name.toLowerCase()}
                                    key={i}
                                 >
                                    <h4>This is {data.name} title</h4>
                                    <p>{data.content}</p>
                                    <p>{data.content}</p>
                                 </Tab.Pane>
                              ))}
                           </Tab.Content>
                        </Tab.Container>
                     </div>
                  </Card.Body>
               </Card>
            </Col>
            <Col xl={6}>
               <Card>
                  <Card.Header>
                     <Card.Title>Nav Pills Tabs</Card.Title>
                  </Card.Header>
                  <Card.Body>
                     <Tab.Container
                        defaultActiveKey={tabData[0].name.toLowerCase()}
                     >
                        <Nav as="ul" className="nav-pills mb-4 light">
                           {tabData.map(
                              (data, i) =>
                                 i !== tabData.length - 1 && (
                                    <Nav.Item as="li" key={i}>
                                       <Nav.Link
                                          eventKey={data.name.toLowerCase()}
                                       >
                                          Tab{" "}
                                          {i === 1
                                             ? "One"
                                             : i === 2
                                             ? "Two"
                                             : "Three"}
                                       </Nav.Link>
                                    </Nav.Item>
                                 )
                           )}
                        </Nav>
                        <Tab.Content className="pt-4">
                           {tabData.map(
                              (data, i) =>
                                 i !== tabData.length - 1 && (
                                    <Tab.Pane
                                       eventKey={data.name.toLowerCase()}
                                       key={i}
                                    >
                                       <p>{data.content}</p>
                                       <p>{data.content}</p>
                                    </Tab.Pane>
                                 )
                           )}
                        </Tab.Content>
                     </Tab.Container>
                  </Card.Body>
               </Card>
            </Col>
            <Col xl={6}>
               <Card>
                  <Card.Header>
                     <Card.Title>Nav Pills Tabs</Card.Title>
                  </Card.Header>
                  <Card.Body>
                     <Tab.Container
                        defaultActiveKey={tabData[0].name.toLowerCase()}
                     >
                        <Nav
                           as="ul"
                           className="nav-pills mb-4 justify-content-end"
                        >
                           {tabData.map(
                              (data, i) =>
                                 i !== tabData.length - 1 && (
                                    <Nav.Item as="li" key={i}>
                                       <Nav.Link
                                          eventKey={data.name.toLowerCase()}
                                       >
                                          Tab{" "}
                                          {i === 1
                                             ? "One"
                                             : i === 2
                                             ? "Two"
                                             : "Three"}
                                       </Nav.Link>
                                    </Nav.Item>
                                 )
                           )}
                        </Nav>
                        <Tab.Content className="pt-4">
                           {tabData.map(
                              (data, i) =>
                                 i !== tabData.length - 1 && (
                                    <Tab.Pane
                                       eventKey={data.name.toLowerCase()}
                                       key={i}
                                    >
                                       <p>{data.content}</p>
                                       <p>{data.content}</p>
                                    </Tab.Pane>
                                 )
                           )}
                        </Tab.Content>
                     </Tab.Container>
                  </Card.Body>
               </Card>
            </Col>

            <Col xl={6}>
               <Card>
                  <Card.Header>
