import React, {useState} from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import CSRFInput from "@ueaweb/laravel-react-csrf-input";

const Registration = () => {
   const [registertate, setRegistertate] = useState(
      {
      name:'',
      phone_number:'',
      email:'',
      password:''
      });
   
   
   const handleInput = (e)=> {
      console.log(registertate);
      setRegistertate({
         [e.target.name]: e.target.value,
      });
      console.log(registertate);


   }
   const checkTheAuth = async (e)=> {
      e.preventDefault();
      console.log(registertate);
      const req = await axios.post('http://localhost/yesilik1/public/api/register', registertate);
      

      if(req.data.status === 200){
         console.log(req.data.Message);
         setRegistertate({
            name:'',
            phone_number:'',
            email:'',
            password:''
         })
      }
   }
   const myToken = document.head.querySelector('meta[name="csrf-token"]').getAttribute('content');
	
   return (
      <div className="row justify-content-center  h-200 align-items-center h-80">
         <div className="col-md-4">
            <div className="authincation-content">
               <div className="row no-gutters">
                  <div className="col-xl-12">
                     <div className="auth-form">
                        <div className="text-center mb-3">
                          <h4 className="text-center mb-4">Sign up your account</h4>
                        </div>
                        
                        <form
                           method="POST"
                           onSubmit={checkTheAuth}

                        >
                           <CSRFInput token={myToken} />

                           <div className="form-group">
                              <label className="mb-1 "> <strong>Full Name</strong> </label>
                              <input
                                 type="text"
                                 className="form-control"
                                 placeholder="Full name"
                                 name="name"
                                 onChange={handleInput}  
                                 value={registertate.name}
                                 
                              />
                           </div>
                           <div className="form-group">
                              <label className="mb-1 "> <strong>Phone</strong> </label>
                              <input
                                 type="text"
                                 className="form-control"
                                 placeholder="phone"
                                 name="phone_number"
                                 onChange={handleInput}  value={registertate.phone_number}
                                 
                              />
                           </div>
                           <div className="form-group">
                              <label className="mb-1 ">   <strong>Email</strong>  </label>
                              <input  
                              type="email"
                              className="form-control"  
                              placeholder="hello@example.com"  
                              name="email"
                              onChange={handleInput}  value={registertate.email}
                              />
                           </div>
                           <div className="form-group">
                              <label className="mb-1 ">   <strong>Password</strong>  </label>
                              <input type="password" 
                              className="form-control" 
                              name="password" 
                              onChange={handleInput}  value={registertate.password}
                              />
                           </div>
                           <div className="text-center mt-4">
                              <button type="submit" className="btn btn-primary btn-block"   >  Sign me up  </button>
                           </div>
                        </form>
                        <div className="new-account mt-3">
                           <p className="">
                              Already have an account?{" "}
                              <Link className="text-primary" to="/">
                                 Sign in
                              </Link>
                           </p>
                        </div>
                     </div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   );
};

export default Registration;
