import React, { Component, componentWillReceiveProps, KeyboardEventHandler } from 'react';

import CreatableSelect from 'react-select/creatable';
import { ActionMeta, OnChangeValue } from 'react-select';

const components = {
  DropdownIndicator: null,
};

interface Option {
   label: string;
   value: string;
}

const createOption = (label: string) => ({
  label,
  value: label,
});

interface State {
   inputValue: string;
   value:  Option[];
}

export default class CreatableInputOnly extends Component<{}, State> {
    constructor(props){
        super(props);
    };
  state: State = {
    inputValue: '',
    value: [],
  };
  handleChange = (
    value: OnChangeValue<Option, true>,
    actionMeta: ActionMeta<Option>
  ) => {
    console.group('Value Changed');
    console.log(value);
    console.log(`action: ${actionMeta.action}`);
    console.groupEnd();
    this.setState({ value });
    // this.props.setArrFunc([...this.props.arr,this.state]);
  };
  handleInputChange = (inputValue: string) => {
    this.setState({ inputValue });

  };
  handleKeyDown: KeyboardEventHandler<HTMLDivElement> = (event) => {
    const { inputValue, value } = this.state;
    if (!inputValue) return;
    switch (event.key) {
      case 'Enter':
      case 'Tab':
        console.group('Value Added');
        
        console.log(value);

        console.groupEnd();
        this.setState({
          inputValue: '',
          value: [...value, createOption(inputValue)],
        });
       
        
        event.preventDefault();
    }
  };
//   componentWillReceiveProps = (nextProps) => {
//      console.log("fdsakfjskld");
//   }
 
  render() {
    const { inputValue, value } = this.state;
    return (
      <div>  
      <CreatableSelect
        components={components}
        inputValue={inputValue}
        isClearable
        isMulti
        menuIsOpen={false}
        onChange={this.handleChange}
        onInputChange={this.handleInputChange}
        onKeyDown={this.handleKeyDown}
        placeholder="Type something and press enter..."
        value={value}
      />
       {
        state?.map((item,i)=>{
            return(
              <div className="row my-1" key={`item-${i}`}>
              <div className="col-xl-5 col-xxl-5 col-lg-5 col-sm-12 ">
                  <input
                      type="text"
                      className="form-control"
                      name="name"
                      placeholder="Name"
                      // value={item.name}
                      // onChange={(e) => onChange(index, e)}
                  />
                  
              </div>
              <div className="col-xl-5 col-xxl-5 col-lg-5 col-sm-12 mt-1">
                  <input
                      type="text"
                      className="form-control"
                      name="value"
                      placeholder="Value"
                      // value={item.value}
                      // onChange={(e) => onChange(index, e)}
                  />
                 
              </div>
              <div className="col-xl-2 col-xxl-2 col-lg-2 col-sm-12 mt-1">
                  <button
                      className="btn btn-warning "
                      // onClick={(e) => handleRemoveField(e, index)}
                  >
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-x-lg" viewBox="0 0 16 16">
                          <path fillRule="evenodd" d="M13.854 2.146a.5.5 0 0 1 0 .708l-11 11a.5.5 0 0 1-.708-.708l11-11a.5.5 0 0 1 .708 0Z" />
                          <path fillRule="evenodd" d="M2.146 2.146a.5.5 0 0 0 0 .708l11 11a.5.5 0 0 0 .708-.708l-11-11a.5.5 0 0 0-.708 0Z" />
                      </svg>
                  </button>
              </div>
          </div>
                
        )

        })
        }
      </div>
    );
  }
}
