import React, { Fragment,useState,useEffect } from "react";
import PageTItle from "../../layouts/PageTitle";
import { Button, Modal,  Form } from "react-bootstrap";
import axios from "axios";
import swal from "sweetalert"
const Inventory = (props) => {
    // insert modal
    const [modalCentered, setModalCentered] = useState(false);
    // edit modal
    const [editmodalCentered, setEditModalCentered] = useState(false);
    // insert a section
    const [variantInsert, setVariantInsert] = useState({
        variant_name: '',
        SellingQuantity: '',
        Buyingquantity: '',
        buyingPrice: '',
        sellingPrice: '',
        Description: '',
        Advice: '',
        CurrentPrice: '',
        OldPrice: '',
        UnitID: '4',
        branch_id: props.location.id
    });
    const handleInput = (e) => {
        e.persist();
        setVariantInsert({...variantInsert, [e.target.name]: e.target.value});
    };
   
    const saveUnit=  (e) => {
        e.preventDefault();
        axios.post("/api/InsertInventory", variantInsert).then(res=>{
            if(res.data.status === 200){
                setVariantInsert({
                    variant_name: '',
                    SellingQuantity: '',
                    Buyingquantity: '',
                    buyingPrice: '',
                    sellingPrice: '',
                    Description: '',
                    Advice: '',
                    CurrentPrice: '',
                    OldPrice: '',
                    UnitID: '4',
                    branch_id: props.location.id
                });
                swal("Success",res.data.message,"success");
                setModalCentered(false)
                //  this.props.history.push("/")
            }
        });
    };
    // edit code
    const [editUnit, setEditUnit] = useState([]);
    const editHandleInput = (e) => {
        e.persist();
        setEditUnit({...editUnit, [e.target.name]: e.target.value});
    };
    const fetchUnit = (e,id)=>{
        e.preventDefault();
        axios.get(`/api/EditUnits/${id}`).then(res=>{
            if(res.data.status === 200){
                console.log(res.data.unit);
                setEditUnit(res.data.unit);
                setEditModalCentered(true);
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }

        });

    }
    const updateUnit =  (e) => {
        e.preventDefault();
        axios.post("/api/UpdateUnit", editUnit).then(res=>{
            if(res.data.status === 200){
                setEditUnit('');
                swal("Success",res.data.message,"success");
                setEditModalCentered(false)
                //  this.props.history.push("/")
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");

            }
        });
        
    };
    





    //for retriving data using laravel API
    const [fetchData,setFetchData]=useState([]);
    const [unitData,setUnitData]=useState([]);
    const [loading, setLoading]=useState(true);
    
    useEffect( () => {
        axios.get(`/api/GetInventory/${props.location.id}`).then(res => {
            if(res.data.status === 200){
                setFetchData(res.data.fetchData);
            }
            setLoading(false);
          });
        axios.get(`/api/GetUnits/${props.location.id}`).then(res => {
            if(res.data.status === 200){
                setUnitData(res.data.fetchData);
            }
         
          });
      }, [props.location.id]);

    var viewProducts_HTMLTABLE = "";
    if(loading){
        return <h4>Loading...!</h4>
    }else{
        viewProducts_HTMLTABLE = 
        fetchData.map((item,i)=>{
            return (
                <tr key={item.id}>
                    <td>{i+1}</td>
                   
                    <td> {item.variant_name}</td>
                    <td> {item.Buyingquantity}</td>
                    <td> {item.SellingQuantity}</td>
                    <td> {item.buyingPrice}</td>
                    <td> {item.sellingPrice}</td>
                    <td> {item.purchase_sell}</td>
                    <td>
                        <button type="button"   onClick={(e)=>fetchUnit(e,item.id)} className="btn btn-outline-danger btn-sm">Edit</button>&nbsp;&nbsp;&nbsp;
                        <button type="button" onClick={(e)=>deleteVariant(e,item.id)} className="btn btn-outline-warning btn-sm">Delete</button>
                    </td> 
                </tr>
            )
        })

    }
    // delete section 
    const deleteVariant= (e,id)=>{
        e.preventDefault();
        axios.delete(`/api/DeleteInventory/${id}`).then(res=>{
            if(res.data.status === 200){
                swal("Success",res.data.message,"success");
                // thisClicked.closest("tr").remove();
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }
        });
    }
    return (
      <Fragment>
         <PageTItle headingPara="Inventory" activeMenu="add-variant" motherMenu="Inventories" />
        {/* <!-- Insert  Modal --> */}
        <Modal className="fade bd-example-modal-lg" show={modalCentered} size="lg">
            <Form onSubmit={saveUnit} method= "POST" encType="multipart/form-data">
                <Modal.Header>
                    <Modal.Title>Add A Variant</Modal.Title>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Branch Name: {props.location.branchName}</strong> </label>
                        </div>
                        <div className="row" >
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Variant Name</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Variant Name"
                                        name="variant_name"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.variant_name}
                                    />
                                </div>
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Buying Quantity</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Buying Quantity"
                                        name="Buyingquantity"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.Buyingquantity}
                                    />
                                </div>
                            </div>
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                               
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Buying Price</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Buying Price"
                                        name="buyingPrice"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.buyingPrice}
                                    />
                                </div>
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Selling Price</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Selling Price"
                                        name="sellingPrice"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.sellingPrice}
                                    />
                                </div>
                            </div>
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                               
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Product Name</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="ProductName"
                                        name="ProductName"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.ProductName}
                                    />
                                </div>
                            </div>
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                               
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Description</strong> </label>
                                    <textarea
                                        type="text"
                                        className="form-control"
                                        placeholder="Buying Price"
                                        name="Description"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.Description}
                                    />
                                </div>
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>Advice</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Advice"
                                        name="Advice"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.Advice}
                                    />
                                </div>
                            </div>
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                               
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>CurrentPrice</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="CurrentPrice"
                                        name="CurrentPrice"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.CurrentPrice}
                                    />
                                </div>
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>OldPrice</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="OldPrice"
                                        name="OldPrice"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.OldPrice}
                                    />
                                </div>
                            </div>
                            <div className="col-xl-6 col-xxl-6 col-lg-6 col-sm-12">
                               
                                {/* <div className="form-group">
                                    <label className="mb-1 "> <strong>UnitID</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="UnitID"
                                        name="UnitID"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.UnitID}
                                    />
                                </div> */}
                                <div className="form-group">
                                    <label className="mb-1 "> <strong>SellingQuantity</strong> </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="SellingQuantity"
                                        name="SellingQuantity"
                                        required
                                        onChange={handleInput}  
                                        value={variantInsert.SellingQuantity}
                                    />
                                </div>
                               
                            </div>

                        </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Save</Button>

                </Modal.Footer>
            </Form>
        </Modal>
         {/* Edit Modal */}
         <Modal className="fade" show={editmodalCentered}>
            <Form onSubmit={updateUnit} method= "POST" >
                <Modal.Header>
                    <Modal.Title>Edit Unit</Modal.Title>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Service Area"
                                name="UnitName"
                                required
                                onChange={editHandleInput}  
                                value={editUnit.UnitName}
                            />
                        </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Update </Button>

                </Modal.Footer>
            </Form>
        </Modal>
         <div className="row" >
            <div className="col-xl-12 col-xxl-12 col-lg-12 col-sm-12">
				<div className="card">
					<div className="card-header border-0">
						<div>
							<h4 className="card-title mb-2">Units</h4>
						</div>
						<div className="dropdown">
							<Button 
                            variant="primary"
                            type="button"
                            className="mb-2 mr-2"
                            onClick={() => setModalCentered(true)} >
								Add Variant
							</Button>
						</div>
					</div>
					<div className="card-body p-0">
						<div className="table-responsive ">
							<table className="table ">
                                <thead>
                                    <tr>
                                        <th>#NO</th>
                                        <th>Variant Name</th>
                                        <th>Buying Quantity</th>
                                        <th>Selling Quantity</th>
                                        <th>Buying Price</th>
                                        <th>Selling Price</th>
                                        <th>Purchase Sell</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
								<tbody>
                                    {viewProducts_HTMLTABLE}
							    </tbody>
                            </table>
						</div>
					</div>
				</div>
			</div>
         </div>
      </Fragment>
   );
};

export default Inventory;
