import React, { useEffect, useState, Fragment } from "react";

import axios from "axios";
import { useTranslation } from "react-i18next";

import { Link as RLink } from "react-router-dom"


const Counter = (props) => {
    const { t } = useTranslation();

    const [quantity, setQuantity] = useState(1);
    const [show, setShow] = useState(true);
    const showFuntion = (e) => {

        setShow(!show);
    }
    const handleDecrement = (e) => {
        e.preventDefault();

        if (quantity > 1) {
            setQuantity(prevCount => prevCount - 1);
        } else if (quantity === 1) {
            setShow(false)
        }
    }
    const handelIncrement = (e) => {
        e.preventDefault();


        setQuantity(prevCount => prevCount + 1);
    }



    return (

        <div >


            <div className={`input-group ${show ? " " : "d-none"}`}>
                <RLink onClick={(e) => handleDecrement(e)} className="input-group-text ">{quantity === 1 ? <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash" viewBox="0 0 16 16">
                    <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z" />
                    <path fillRule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z" />
                </svg> : t('minus')} </RLink>
                <div className="input-group-text bg-white "> {quantity}</div>
                <RLink onClick={(e) => handelIncrement(e)} className="input-group-text" >{t('plus')} </RLink>
            </div>

            {/* {item.Buyingquantity - item.SellingQuantity === quantity
                ?
                <div className="text-danger">
                    {t('not_available')}
                </div>
                : ""
            } */}
        </div>
    );

};

export default Counter;
