import React, { Component, KeyboardEventHandler } from 'react';

import CreatableSelect from 'react-select/creatable';
import { ActionMeta, OnChangeValue } from 'react-select';

const components = {
  DropdownIndicator: null,
};

interface Option {
   label: string;
   value: string;
}

const createOption = (label: string) => ({
  label,
  value: label,
});

interface State {
   inputValue: string;
   value:  Option[];
}

export default class CreatableInputOnly extends Component<{}, State> {
  state: State = {
    inputValue: '',
    value: [],
  };
  handleChange = (
    value: OnChangeValue<Option, true>,
    actionMeta: ActionMeta<Option>
  ) => {
    console.group('Value Changed');
    console.log(value);
    console.log(`action: ${actionMeta.action}`);
    console.groupEnd();
    this.setState({ value });
  };
  handleInputChange = (inputValue: string) => {
    this.setState({ inputValue });
  };
  handleKeyDown: KeyboardEventHandler<HTMLDivElement> = (event) => {
    const { inputValue, value } = this.state;
    if (!inputValue) return;
    switch (event.key) {
      case 'Enter':
      case 'Tab':
        console.group('Value Added');
        console.log(value);
        console.groupEnd();
        this.setState({
          inputValue: '',
          value: [...value, createOption(inputValue)],
        });
        event.preventDefault();
    }
  };
  render() {
    const { inputValue, value } = this.state;
    return (
      <div>  
      <CreatableSelect
        components={components}
        inputValue={inputValue}
        // isClearable
        isMulti
        menuIsOpen={false}
        onChange={this.handleChange}
        onInputChange={this.handleInputChange}
        onKeyDown={this.handleKeyDown}
        placeholder="Type something and press enter..."
        value={value}
      />
       {
        value.map((item,i)=>{
            return(
                <div className="col-xl-12 col-xxl-12 col-lg-12 col-sm-12" key={i}>
            <div className="form-group">
                <label className="mb-1 "> <strong>{item.label}</strong> </label>
            </div>
        </div>
                
        )

        })
        }
      </div>
    );
  }
}
