import React,{Fragment,useEffect, useState } from 'react';
import {Link} from "react-router-dom"


import axios from "axios";

const Order = () => {
    //for retriving data using laravel API
const [fetchData,setFetchData]=useState([]);
const [loading, setLoading]=useState(true);

useEffect( () => {
    axios.get(`/api/Getvariations`).then(res => {
        if(res.data.status === 200){
            setFetchData(res.data.fetchData);
            console.log(fetchData);
        }
        setLoading(false);
      });
  }, []);

var viewVariants_HTMLTABLE = "";
if(loading){
    return <h4>Loading...!</h4>
}else{
    viewVariants_HTMLTABLE = 
    fetchData.map((item,i)=>{
        return (
                <div className="col-xl-3 col-lg-6 col-md-6 col-sm-6" key={item.id}>
                    <div className="card">
                        <div className="card-body">
                        <div className="new-arrival-product">
                            <div className="new-arrivals-img-contnent">
                                <img className="img-fluid" src={`http://localhost:8000/images/variants_pics/${item.PicturesLocation}`} alt="" />
                            </div>
                            <div className="new-arrival-content text-center mt-3">
                                <h4>
                                    <Link to={`/gallery/${item.variantID}`}  className="text-black"> {item.VariationName}</Link>
                                </h4>
                                {item.Description}
                                <span className="price">$13405</span>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
        )
    })

}
	return (
        <Fragment>
            <div className="row">
                {viewVariants_HTMLTABLE}
            </div>
        </Fragment>
					
	);
}
export default Order;
