import React, { Fragment,useState,useEffect } from "react";
import PageTItle from "../../layouts/PageTitle";
import { Button, Modal,  Form } from "react-bootstrap";
import axios from "axios";
import swal from "sweetalert"
import state from "sweetalert/typings/modules/state";
const Branches = () => {
    // state for modal
    const [modalCentered, setModalCentered] = useState(false);
    // insert a branch
    const [branchstate, setBranchstate] = useState({
        BrancheName: '',
    });
    const handleInput = (e) => {
        e.preventDefault();
        setBranchstate({...branchstate, [e.target.name]: e.target.value});
    };
    const saveBranch =  (e) => {
        e.preventDefault();
        const data={
            BrancheName:branchstate.BrancheName
        }
        axios.post("/api/InsertBranches", data).then(res=>{
            if(res.data.status === 200){
                // console.log(res.data.status);
                setBranchstate({
                    BrancheName: '',
                 });
                 swal("Success",res.data.message,"success");
                 setModalCentered(false)
                //  this.props.history.push("/")
            }
        });
        
    };
    //for retriving data using laravel API
    const [branchdata,setBranchdata]=useState([]);
    const [loading, setLoading]=useState(true);
    useEffect( () => {
        axios.get('/api/GetBranches').then(res => {
            if(res.data.status === 200)
            setBranchdata(res.data.branches);
            setLoading(false);
          });
      });
    var viewBranches_HTMLTABLE = "";
    if(loading){
        return <h4>Loading...!</h4>
    }else{
        viewBranches_HTMLTABLE = 
        branchdata.map((item)=>{
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.BrancheName}</td>
                </tr>
            )
        })

    }
    // delete branch 
    const deleteBranch= (e,id)=>{
        e.preventDefault();
        const thisClicked = e.currentTarget;
        thisClicked.innerTex = "Deleting";
        
        axios.delete(`/api/DeleteBranches/${id}`).then(res=>{
            if(res.data.status === 200){
                swal("Success",res.data.message,"success");
                thisClicked.closest("tr").remove();
            }else if(res.data.status === 404){
                swal("Success",res.data.message,"success");
                thisClicked.innerTex = "Delete";

            }

        });

    }

    return (
      <Fragment>
         <PageTItle headingPara="Branches" activeMenu="add-branch" motherMenu="Branch" />
        {/* <!-- Modal --> */}
        <Modal className="fade" show={modalCentered}>
            <Form onSubmit={saveBranch} method= "POST" >
                <Modal.Header>
                    <Modal.Title>Add A Branch</Modal.Title>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Branch Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Branch Name"
                                name="BrancheName"
                                required
                                onChange={handleInput}  
                                value={branchstate.BrancheName}
                            />
                        </div>

                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Save </Button>

                </Modal.Footer>
            </Form>

        </Modal>
         <div className="row" >
            <div className="col-xl-12 col-xxl-12 col-lg-12 col-sm-12">
				<div className="card">
					<div className="card-header border-0">
						<div>
							<h4 className="card-title mb-2">Branches</h4>
						</div>
						<div className="dropdown">
							<Button 
                            variant="primary"
                            type="button"
                            className="mb-2 mr-2"
                            onClick={() => setModalCentered(true)} >
								Add
							</Button>
							
						</div>
					</div>
					<div className="card-body p-0">
						<div className="table-responsive ">
							<table className="table ">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Branch Name</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
								<tbody>
                                {/* {branchdata.branches.map((branch) => (
                                    
                                    <tr key={branch.id}>
                                        <td >
                                            <h5>{branch.id}</h5>
                                        </td>
                                        <td>
                                            <h5>{branch.BrancheName}</h5>
                                        </td> 
                                        <td>
                                            <button type="button" className="btn btn-outline-danger btn-sm">Edit</button>&nbsp;&nbsp;&nbsp;
                                            <button type="button" onClick={(e)=>deleteBranch(e,branch.id)} className="btn btn-outline-warning btn-sm">Delete</button>
                                        </td> 
                                    </tr>	
                                    
                                   ))}											 */}
							    </tbody>
                            </table>
						</div>
					</div>
				</div>
			</div>
         </div>
      </Fragment>
   );
};

export default Branches;
