import React, { Fragment,useState,useEffect } from "react";
import PageTItle from "../../layouts/PageTitle";
import { Button, Modal,  Form } from "react-bootstrap";
import {Link } from "react-router-dom"
import axios from "axios";
import swal from "sweetalert"
const SubMenu = (props) => {
    // insert modal
    const [modalCentered, setModalCentered] = useState(false);
    // edit modal
    const [editmodalCentered, setEditModalCentered] = useState(false);
    // insert a section

    const [subCategoryInsert, setSubCategoryInsert] = useState('');

    const handleInput = (e) => {
        e.persist();
        setSubCategoryInsert({...subCategoryInsert, [e.target.name]: e.target.value});
    };
   
    const saveSubMenu=  (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('CategoryName', categoryInsert.CategoryName);
        formData.append('branchID', categoryInsert.branchID);
        axios.post("/api/InsertSubCategories", subCategoryInsert).then(res=>{
            if(res.data.status === 200){
                // console.log(res.data.status);
                setSubCategoryInsert('');
                swal("Success",res.data.message,"success");
                setModalCentered(false)
                //  this.props.history.push("/")
            }
        });
        
    };
    // edit code
    const [editMenu, setEditMenu] = useState('');
    const editHandleInput = (e) => {
        e.persist();
        setEditMenu({...editMenu, [e.target.name]: e.target.value});
    };
    const fetchMenus = (e,id)=>{
        e.preventDefault();
        axios.get(`/api/EditCategories/${id}`).then(res=>{
            if(res.data.status === 200){
                setEditMenu(res.data.menu);
                setEditModalCentered(true);
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }

        });

    }
    const updateProduct =  (e) => {
        e.preventDefault();
        axios.post("/api/UpdateCategories", editMenu).then(res=>{
            if(res.data.status === 200){
                // console.log(res.data.status);
                setEditMenu('');
                swal("Success",res.data.message,"success");
                setEditModalCentered(false)
                //  this.props.history.push("/")
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");

            }
        });
        
    };
    





    //for retriving data using laravel API
    const [fetchData,setFetchData]=useState([]);
    const [loading, setLoading]=useState(true);
    
    useEffect( () => {
        axios.get(`/api/GetSubCategories/${props.location.id}`).then(res => {
            if(res.data.status === 200){
                setFetchData(res.data.fetchData);
            }
            setLoading(false);
          });
      }, [props.location.id]);

    var viewProducts_HTMLTABLE = "";
    if(loading){
        return <h4>Loading...!</h4>
    }else{
        viewProducts_HTMLTABLE = 
        fetchData.map((item,i)=>{
            return (
                <tr key={item.id}>
                    <td>{i+1}</td>
                    <Link to={{
                        pathname: '/menu',
                        id:item.id,
                        SubCategoryName:item.SubCategoryName }}>
                    <td>{item.SubCategoryName}</td>
                    </Link>
                    <td>
                        <button type="button"   onClick={(e)=>fetchMenus(e,item.id)} className="btn btn-outline-danger btn-sm">Edit</button>&nbsp;&nbsp;&nbsp;
                        <button type="button" onClick={(e)=>deleteMenu(e,item.id)} className="btn btn-outline-warning btn-sm">Delete</button>
                    </td> 
                </tr>
            )
        })

    }
    // delete section 
    const deleteMenu= (e,id)=>{
        e.preventDefault();
        axios.delete(`/api/DeleteCategories/${id}`).then(res=>{
            if(res.data.status === 200){
                swal("Success",res.data.message,"success");
                // thisClicked.closest("tr").remove();
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }
        });

    }
    
    return (
      <Fragment>
         <PageTItle headingPara="Sub-Menu" activeMenu="add-sub-menu" motherMenu="SubMenus" />
        {/* <!-- Insert  Modal --> */}
        <Modal className="fade" show={modalCentered}>
            <Form onSubmit={saveSubMenu} method= "POST" encType="multipart/form-data">
                <Modal.Header>
                    <Modal.Title>Add A SubMenu</Modal.Title>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Menu Name: {props.location.CategoryName}</strong> </label>
                        </div>
                        
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Sub Menu Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Sub Menu Name"
                                name="SubCategoryName"
                                required
                                onChange={handleInput}  
                                value={subCategoryInsert.SubCategoryName}
                            />
                        </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Save</Button>

                </Modal.Footer>
            </Form>
        </Modal>
         {/* Edit Modal */}
         <Modal className="fade" show={editmodalCentered}>
            <Form onSubmit={updateProduct} method= "POST" >
                <Modal.Header>
                    <Modal.Title>Edit Menu</Modal.Title>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>ID</strong> </label>
                            <input
                                type="text"
                                disabled="disabled"
                                className="form-control"
                                name="id"
                                required
                                onChange={editHandleInput}  
                                value={editMenu.id}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Menu Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Menu Name"
                                name="CategoryName"
                                required
                                onChange={editHandleInput}  
                                value={editMenu.CategoryName}
                            />
                        </div>

                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Update </Button>

                </Modal.Footer>
            </Form>
        </Modal>
         <div className="row" >
            <div className="col-xl-12 col-xxl-12 col-lg-12 col-sm-12">
				<div className="card">
					<div className="card-header border-0">
						<div>
							<h4 className="card-title mb-2">Sub Menus</h4>
						</div>
						<div className="dropdown">
							<Button 
                            variant="primary"
                            type="button"
                            className="mb-2 mr-2"
                            onClick={() => setModalCentered(true)} >
								Add Sub-Menu
							</Button>
						</div>
					</div>
					<div className="card-body p-0">
						<div className="table-responsive ">
							<table className="table ">
                                <thead>
                                    <tr>
                                        <th>#NO</th>
                                        <th>Sub Menu Name</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
								<tbody>
                                    {viewProducts_HTMLTABLE}
							    </tbody>
                            </table>
						</div>
					</div>
				</div>
			</div>
         </div>
      </Fragment>
   );
};

export default SubMenu;
