    const [variantInsert, setVariantInsert] = useState({
        variant_name: '',
        Buyingquantity: '',
        SellingQuantity: '',
        buyingPrice: '',
        sellingPrice: '',
        purchase_sell: '',
        brancheID: props.location.id
    });
    const handleInput = (e) => {
        e.persist();
        setVariantInsert({...variantInsert, [e.target.name]: e.target.value});
    };
   
    const saveUnit=  (e) => {
        e.preventDefault();
        axios.post("/api/InsertUnits", variantInsert).then(res=>{
            if(res.data.status === 200){
                setVariantInsert({
                    variant_name: '',
                    Buyingquantity: '',
                    SellingQuantity: '',
                    buyingPrice: '',
                    sellingPrice: '',
                    purchase_sell: '',
                    brancheID: props.location.id
                });
                swal("Success",res.data.message,"success");
                setModalCentered(false)
                //  this.props.history.push("/")
            }
        });
    };
    // edit code
    const [editUnit, setEditUnit] = useState([]);
    const editHandleInput = (e) => {
        e.persist();
        setEditUnit({...editUnit, [e.target.name]: e.target.value});
    };
    const fetchUnit = (e,id)=>{
        e.preventDefault();
        axios.get(`/api/EditUnits/${id}`).then(res=>{
            if(res.data.status === 200){
                console.log(res.data.unit);
                setEditUnit(res.data.unit);
                setEditModalCentered(true);
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }

        });

    }
    const updateUnit =  (e) => {
        e.preventDefault();
        axios.post("/api/UpdateUnit", editUnit).then(res=>{
            if(res.data.status === 200){
                setEditUnit('');
                swal("Success",res.data.message,"success");
                setEditModalCentered(false)
                //  this.props.history.push("/")
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");

            }
        });
        
    };
    





    //for retriving data using laravel API
    const [fetchData,setFetchData]=useState([]);
    const [loading, setLoading]=useState(true);
    
    useEffect( () => {
        axios.get(`/api/GetInventory/${props.location.id}`).then(res => {
            if(res.data.status === 200){
                setFetchData(res.data.fetchData);
            }
            setLoading(false);
          });
      }, [props.location.id]);

    var viewProducts_HTMLTABLE = "";
    if(loading){
        return <h4>Loading...!</h4>
    }else{
        viewProducts_HTMLTABLE = 
        fetchData.map((item,i)=>{
            return (
                <tr key={item.id}>
                    <td>{i+1}</td>
                   
                    <td> {item.variant_name}</td>
                    <td> {item.Buyingquantity}</td>
                    <td> {item.SellingQuantity}</td>
                    <td> {item.buyingPrice}</td>
                    <td> {item.sellingPrice}</td>
                    <td> {item.purchase_sell}</td>
                    <td>
                        <button type="button"   onClick={(e)=>fetchUnit(e,item.id)} className="btn btn-outline-danger btn-sm">Edit</button>&nbsp;&nbsp;&nbsp;
                        <button type="button" onClick={(e)=>deleteVariant(e,item.id)} className="btn btn-outline-warning btn-sm">Delete</button>
                    </td> 
                </tr>
            )
        })

    }
    // delete section 
    const deleteVariant= (e,id)=>{
        e.preventDefault();
        axios.delete(`/api/DeleteInventory/${id}`).then(res=>{
            if(res.data.status === 200){
                swal("Success",res.data.message,"success");
                // thisClicked.closest("tr").remove();
            }else if(res.data.status === 404){
                swal("Error",res.data.message,"error");
            }
        });
    }
    return (
      <Fragment>
         <PageTItle headingPara="Inventory" activeMenu="add-variant" motherMenu="Inventories" />
        {/* <!-- Insert  Modal --> */}
        <Modal className="fade" show={modalCentered}>
            <Form onSubmit={saveUnit} method= "POST" encType="multipart/form-data">
                <Modal.Header>
                    <Modal.Title>Add A Variant</Modal.Title>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Branch Name: {props.location.branchName}</strong> </label>
                        </div>
                        
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Unit Name"
                                name="UnitName"
                                required
                                onChange={handleInput}  
                                value={unitInsert.UnitName}
                            />
                        </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Save</Button>

                </Modal.Footer>
            </Form>
        </Modal>
         {/* Edit Modal */}
         <Modal className="fade" show={editmodalCentered}>
            <Form onSubmit={updateUnit} method= "POST" >
                <Modal.Header>
                    <Modal.Title>Edit Unit</Modal.Title>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant=""
                        className="close"
                    >
                        <span>&times;</span>
                    </Button>
                </Modal.Header>
                <Modal.Body>
                        <div className="form-group">
                            <label className="mb-1 "> <strong>Unit Name</strong> </label>
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Service Area"
                                name="UnitName"
                                required
                                onChange={editHandleInput}  
                                value={editUnit.UnitName}
                            />
                        </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => setEditModalCentered(false)}
                        variant="danger light"
                    >
                        Close
                    </Button>
                    <Button variant="primary" type="submit">Update </Button>

                </Modal.Footer>
            </Form>
        </Modal>
         <div className="row" >
            <div className="col-xl-12 col-xxl-12 col-lg-12 col-sm-12">
				<div className="card">
					<div className="card-header border-0">
						<div>
							<h4 className="card-title mb-2">Units</h4>
						</div>
						<div className="dropdown">
							<Button 
                            variant="primary"
                            type="button"
                            className="mb-2 mr-2"
                            onClick={() => setModalCentered(true)} >
								Add Variant
							</Button>
						</div>
					</div>
					<div className="card-body p-0">
						<div className="table-responsive ">
							<table className="table ">
                                <thead>
                                    <tr>
                                        <th>#NO</th>
                                        <th>Variant Name</th>
                                        <th>Buying Quantity</th>
                                        <th>Selling Quantity</th>
                                        <th>Buying Price</th>
                                        <th>Selling Price</th>
                                        <th>Purchase Sell</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
								<tbody>
                                    {viewProducts_HTMLTABLE}
							    </tbody>
                            </table>
						</div>
					</div>
				</div>
			</div>
         </div>
      </Fragment>
   );
};

export default Inventory;
