import React,{Fragment,useEffect, useState } from 'react';
import {Link} from "react-router-dom"


import axios from "axios";

const Order = () => {
    //for retriving data using laravel API
const [fetchData,setFetchData]=useState([]);
const [loading, setLoading]=useState(true);

useEffect( () => {
    axios.get(`/api/Getvariations`).then(res => {
        if(res.data.status === 200){
            setFetchData(res.data.fetchData);
            console.log(fetchData);
        }
        setLoading(false);
      });
  }, []);

var viewVariants_HTMLTABLE = "";
if(loading){
    return <h4>Loading...!</h4>
}else{
    viewVariants_HTMLTABLE = 
    fetchData.map((item,i)=>{
        return (
            <div className="col-xl-4 col-lg-6 col-sm-6" key={item.id}>
                <div className="card overflow-hidden">
                    <div className="card-body">
                        <div className="text-center">
                        <div className="profile-photo">
                            <img
                                src={`http://localhost:8000/images/variants_pics/${item.PicturesLocation}`}
                                className="d-block w-100"
                                alt=""
                            />
                        </div>
                        <h3 className="mt-4 mb-1"><Link to={`/gallery/${item.variantID}`}> {item.VariationName}</Link></h3>
                        <p className="text-muted">{item.Description} </p>
                        {/* <p className="text-muted">{item.SubCategoryName}</p> */}
                      
                       
                        </div>
                    </div>
    
                    <div className="card-footer pt-0 pb-0 text-center">
                        <div className="row">
                            <div className="col-4 pt-3 pb-3 border-right">
                                <Link
                                     onClick={(e)=>fetchVariant(e,item.variantID)}
                                >
                                    <span>Edit</span>
                                </Link>
                            </div>
                            <div className="col-4 pt-3 pb-3 border-right">
                                <Link
                                    onClick={(e)=>deleteVariant(e,item.variantID)}
                                >
                                    <span>Delete</span>
                                </Link>
                            </div>
                            <div className="col-4 pt-3 pb-3">
                                <Link
                                     to={{
                                        pathname: '/variants',
                                        id:item.id,
                                        ProductName:item.ProductName }}
                                >
                                    <span>variants</span>
                                </Link>
                            </div>
                        
                        
                        </div>
                    </div>
                </div>
            </div>
           
        )
    })

}
	return (
        <Fragment>
            <div className="row">
                <div className="col-xl-3 col-lg-6 col-md-6 col-sm-6">
                    <div className="card">
                        <div className="card-body">
                        <div className="new-arrival-product">
                            <div className="new-arrivals-img-contnent">
                                <img className="img-fluid" src="" alt="" />
                            </div>
                            <div className="new-arrival-content text-center mt-3">
                                <h4>
                                    <Link to="/ecom-product-detail"  className="text-black">title</Link>
                                </h4>
                                1343244
                                <span className="price">$13405</span>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
                {viewVariants_HTMLTABLE}
            </div>
        </Fragment>
					
	);
}
export default Order;
