import React, { Fragment,useState,useEffect } from "react";
import PageTItle from "../../layouts/PageTitle";
import { Button, Modal,  Form } from "react-bootstrap";
import {Link } from "react-router-dom"
import axios from "axios";
const VariantDetails = (props) => {
   
    
    // let { id } = useParams();
    const id =props.match.params.id;
    
    //for retriving data using laravel API
    const [fetchData,setFetchData]=useState([]);
    const [variant,setVariant]=useState('');
    const [loading, setLoading]=useState(true);
   
    useEffect( () => {
        axios.get(`/api/GetPictures/${id}`).then(res => {
            if(res.data.status === 200){
                setFetchData(res.data.fetchData);
            }
            setLoading(false);
          });
          axios.get(`/api/Getvariat/${id}`).then(res => {
            if(res.data.status === 200){
                setVariant(res.data.variantdata);
                console.log(variant);
            }
          });
       
      }, [id]);

    var viewImages_HTMLTABLE = "";
    if(loading){
        return <h4>Loading...!</h4>
    }else{
        viewImages_HTMLTABLE = 
        fetchData.map((item,i)=>{
            return (
                <div className="col-xl-6 col-lg-6 col-sm-6" key={item.id}>
                    <div className="card overflow-hidden">
                        {/* <div className="card-body"> */}
                            <div className="text-center">
                                <div className="profile-photo">
                                    <img
                                        src={`http://localhost:8000/images/variants_pics/${item.PicturesLocation}`}
                                        className="d-block w-100 img-thumbnail" 
                                        alt=""
                                    />
                                </div>
                            </div>
                        {/* </div> */}
        
                        
                    </div>
                </div>
            )
        })

    }
    
    

    return (
      <Fragment>
        {/* <!-- Insert  Modal --> */}
        <div className="row" >
            <div className="col-xl-8 col-xxl-8 col-lg-12 col-sm-12">
                <div className="row" >
                    {viewImages_HTMLTABLE}
					
			    </div>
			</div>
            <div className="col-xl-4 col-xxl-4 col-lg-12 col-sm-12">
                <h3>{variant.VariationName}</h3>
            </div>
         </div>
        
      </Fragment>
   );
};

export default VariantDetails;
