      dots: false,
      // className: "center",
      centerMode: true,
      infinite: true,
      swipeToSlide: true,
      centerPadding: "10px",
      slidesToShow: subcategories.length >= 7 ? 7 : subcategories.length,
      slidesToScroll: 1,
      speed: 1000,
      responsive: [
         {
            breakpoint: 1024,
            settings: {
               slidesToShow: subcategories.length >= 5 ? 5 : subcategories.length,
               slidesToScroll: 1,
               infinite: true,
               dots: true,
               speed: 1000,

            }
         },
         {
            breakpoint: 600,
            settings: {
               slidesToShow: subcategories.length >= 2 ? 2 : 1,
               slidesToScroll: 1,
               initialSlide: 1,
               speed: 1000

            }
         },
         {
            breakpoint: 480,
            settings: {
               speed: 1000,

               slidesToShow: subcategories.length >= 1 ? 1 : 1,
               slidesToScroll: 1
            }
         }
      ]
   };


   return (

      <div style={{ marginTop: 5 }}>
         <Fragment>

            <CNavbar expand="lg" colorScheme="light" className="bg-light" placement="sticky-top" >
               {/* <Row> */}
               {/* <Col lg={12}> */}
               <CContainer fluid>
                  {data.map((item, i) => (
                     <CNavbarBrand className="text-center text-uppercase font-weight-bold text-primary" key={i}>{item.BrancheName}</CNavbarBrand>
                  ))}
                  <CNavbarToggler
                     aria-label="Toggle navigation"
                     aria-expanded={visible}
                     onClick={() => setVisible(!visible)}
                  />
                  <CCollapse className="navbar-collapse" visible={visible}>
                     <CNavbarNav>
                        {categories.map((data, i) => (
                           <CNavItem as="li" key={i} >
                              <CNavLink href="#"
                                 onClick={() => filterCategory(data.id)}

                                 className={`text-capitalize font-weight-bold ${activeCategory === data.id ? "active bg-primary text-white" : " "}`}
                              >
                                 {data.CategoryName}
                              </CNavLink>
                           </CNavItem>
                        ))}
                     </CNavbarNav>
                  </CCollapse>
               </CContainer>
               {/* </Col> */}

               {/* </Row> */}
            </CNavbar>
            <CNavbar colorScheme="white" className="bg-white" placement="sticky-top" >

               {/* <Col lg={12}> */}
                  <Card  >
                     <Card.Body>
                        <Slider {...settings}>
                           {subcategories.map((item, i) => (
                              <div key={i} className="px-1 text-center text-capitalize" >
                                 <Nav.Item as="li"  >
                                    <Link
                                       onClick={() => filterProducts(item.sub_id)}
                                       smooth={true}
                                       duration={1000}
                                       to={`${item.SubCategoryName}`}
                                       // eventKey={item.SubCategoryName.toLowerCase()}
                                       className={`text-capitalize font-weight-bold ${activeSubCategory === item.sub_id ? "active border border-primary text-primary   " : " "}`}
                                    >
                                       <div>
                                          <img className={`w-100 img-thumbnail mt-1 mx-1 ${activeSubCategory === item.sub_id ? "border border-primary" : " "}`} style={{ height: '60px', objectFit: 'contain' }} src={`http://192.168.1.103/yesilik1/public/images/sub_catagories/${item.SubCategoryIcon}`} alt="" />
                                       </div>
                                       <div className="mt-2">
                                          {item.SubCategoryName}
                                       </div>
                                    </Link>
                                 </Nav.Item>
                              </div>
                           )
                           )}
                        </Slider>
                     </Card.Body>
                  </Card>
               {/* </Col> */}
            </CNavbar>

         </Fragment>

         <InfiniteScroll
            dataLength={variants.length} //This is important field to render the next data
            next={fetchMoreData}
            hasMore={changeState}
            loader={<p className="text-center pt-5"><b>{t('loading')}</b></p>}
            endMessage={
               <p style={{ textAlign: 'center ' }} className="mb-5">
                  <b>{t('yay_you_have_seen_it_all')}</b>
               </p>
            }
         >
            <div className="row mt-2 mx-2">

               {viewShow_HTMLTABLE}
            </div>
         </InfiniteScroll>
         <CNavbar expand="lg" colorScheme="light" className="row bg-light" placement="fixed-bottom" >
                            
                            <div className="col-xl-4 col-xxl-4 col-lg-4 col-sm-12 mx-5">
                                    <strong>{t('grand_total')}</strong> 
                                    <p className="text-dark">Free</p>
                                   
                            </div>
                            <div className="col-xl-4 col-xxl-4 col-lg-4 col-sm-12 ml-4">
                                    <strong>{t('delivery_fee')}</strong> 
                                    <p className="text-dark">Free</p>
                            </div>
                            <div className="col-xl-4 col-xxl-4 col-lg-4 col-sm-12 ">
                                    <Button variant="outline-success"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-whatsapp" viewBox="0 0 16 16">
  <path d="M13.601 2.326A7.854 7.854 0 0 0 7.994 0C3.627 0 .068 3.558.064 7.926c0 1.399.366 2.76 1.057 3.965L0 16l4.204-1.102a7.933 7.933 0 0 0 3.79.965h.004c4.368 0 7.926-3.558 7.93-7.93A7.898 7.898 0 0 0 13.6 2.326zM7.994 14.521a6.573 6.573 0 0 1-3.356-.92l-.24-.144-2.494.654.666-2.433-.156-.251a6.56 6.56 0 0 1-1.007-3.505c0-3.626 2.957-6.584 6.591-6.584a6.56 6.56 0 0 1 4.66 1.931 6.557 6.557 0 0 1 1.928 4.66c-.004 3.639-2.961 6.592-6.592 6.592zm3.615-4.934c-.197-.099-1.17-.578-1.353-.646-.182-.065-.315-.099-.445.099-.133.197-.513.646-.627.775-.114.133-.232.148-.43.05-.197-.1-.836-.308-1.592-.985-.59-.525-.985-1.175-1.103-1.372-.114-.198-.011-.304.088-.403.087-.088.197-.232.296-.346.1-.114.133-.198.198-.33.065-.134.034-.248-.015-.347-.05-.099-.445-1.076-.612-1.47-.16-.389-.323-.335-.445-.34-.114-.007-.247-.007-.38-.007a.729.729 0 0 0-.529.247c-.182.198-.691.677-.691 1.654 0 .977.71 1.916.81 2.049.098.133 1.394 2.132 3.383 2.992.47.205.84.326 1.129.418.475.152.904.129 1.246.08.38-.058 1.171-.48 1.338-.943.164-.464.164-.86.114-.943-.049-.084-.182-.133-.38-.232z"/>
</svg>  {t('order_now_by_whatsapp')}
</Button>
                                   
                            </div>
         </CNavbar>
      </div>
      
      
   );

};

export default ShowBranchDetails;
