import React, { useEffect, useState } from "react";
import axios from "axios";
import { useTranslation } from "react-i18next";
import { Navbar, Container,  Nav } from "react-bootstrap";

const ShowBranchDetails = (props) => {
	const { t } = useTranslation();
   const branchId =atob(props.match.params.id);
   // atob(branchNameId)
   const [loading, setLoading]= useState(true); 
   const [data, setData]= useState([])
   const [categories, setCategories]= useState([])
   useEffect(()=>{

      axios.get(`/api/GetBranchForShow/${branchId}`).then(res => {
         if(res.data.status === 200){
            setData(res.data.data);
         }
         setLoading(false);
       });
      // axios.get(`/api/GetCategories/${branchId}`).then(res => {
      //    if(res.data.status === 200){
      //       setCategories(res.data.fetchData);
      //    }
      //  });
      

   },[])
   var viewShow_HTMLTABLE="";
   if(loading){
      return <h4>{t('loading')}</h4>
  }else{
      viewShow_HTMLTABLE = 
      data.map((item,i)=>{
          return (
              <div className="col-xl-4 col-lg-6 col-sm-6" key={i}>
               {item.BrancheName}
              </div>
          )
      })
  }
   
   return (
      <Navbar bg="success" variant="dark">
          {data.map((item,i)=>{
             return(
               <Navbar.Brand to="#" key={i}>{item.BrancheName}</Navbar.Brand>
             )
          }) }
         <Nav className="me-auto">
            {/* {categories.map((item,i)=>{
               return(
                  <Nav.Link href="#home" key={i}>{item.CategoryName}</Nav.Link>
               )
            }) } */}
           
         </Nav>

         {viewShow_HTMLTABLE}
      </Navbar>
      // <div className="row justify-content-center  h-200 align-items-center h-80">
      // </div>
   );

};

export default ShowBranchDetails;
